class Letter(val char: Char) {
    var nw: Letter? = null
    var n:  Letter? = null
    var ne: Letter? = null
    var w:  Letter? = null
    var e:  Letter? = null
    var sw: Letter? = null
    var s:  Letter? = null
    var se: Letter? = null
}

val letters: List<List<Letter>> = wordSearch.let {
    val mutList: MutableList<MutableList<Letter>> = mutableListOf()

    for (i in it.indices) {
        val row: MutableList<Letter> = mutableListOf()
        mutList.add(row)
        for (j in it[i].indices) {
            val letter = Letter(it[i][j])
            if (i-1 in mutList.indices) {
                val prevRow = mutList[i-1]
                if (j-1 in prevRow.indices) {
                    letter.nw = prevRow[j-1]
                    letter.nw!!.se = letter
                }
                letter.n = prevRow[j]
                letter.n!!.s = letter
                if (j+1 in prevRow.indices) {
                    letter.ne = prevRow[j+1]
                    letter.ne!!.sw = letter
                }
            }
            if (j-1 in row.indices) {
                letter.w = row[j-1]
                letter.w!!.e = letter
            }
            mutList[i].add(letter)
        }
    }

    mutList
}

val problem1Solution: Int = letters.let {

    var count = 0
    val directions = listOf(
        Letter::nw,
        Letter::n,
        Letter::ne,
        Letter::w,
        Letter::e,
        Letter::sw,
        Letter::s,
        Letter::se,
    )

    for (i in it.indices) {
        for (j in it[i].indices) {
            directions.forEach { dir ->
                val first: Letter = it[i][j]
                val second: Letter? = first?.let(dir.getter)
                val third: Letter? = second?.let(dir.getter)
                val fourth: Letter? = third?.let(dir.getter)
                if (listOf(first, second, third, fourth).map { it?.char } == listOf('X', 'M', 'A', 'S')) count++
            }
        }
    }

    count
}.also(::println)

val problem2Solution: Int = letters.let {

    var count = 0

    for (i in it.indices) {
        for (j in it[i].indices) {
            val letter = it[i][j]
            if (
                letter.char == 'A'
                && listOf(letter.nw?.char ?: '.', letter.se?.char ?: '.').sorted() == listOf('M', 'S')
                && listOf(letter.ne?.char ?: '.', letter.sw?.char ?: '.').sorted() == listOf('M', 'S')
                ) {
                count++
            }
        }
    }

    count
}.also(::println)
